        parameter(npix=496)        parameter(nlin=768)        integer*2 image16(npix,nlin),nbit,ilen        byte image8(npix,nlin)        character*100 filename	write(*,*)'imput image name to be read'        read(*,*)filename        write(*,*)'is image 8bit or 16bit?'        read(*,*)nbit        if (nbit.eq.8)ilen=1        if (nbit.eq.16)ilen=2	open(1,file=filename,status='old',form='unformatted',access=     +       'direct',recl=npix*ilen)        if (nbit.eq.16)then	 do iy=1,nlin	  read(1,rec=iy)(image16(ix,iy),ix=1,npix)	 enddo                elseif(nbit.eq.8)then	 do ix=1,nlin	  read(1,rec=ix)(image8(ix,iy),iy=1,npix)	 enddo        endif        close(1)        end